// Extract and validate input data
const inputData = $input.all()[0].json;

const query = inputData.query || inputData.sql_query || '';
const maxSources = inputData.max_sources || 5;
const includeScores = inputData.include_scores || true;
const contextType = inputData.context_type || 'all';

// Validate required fields
if (!query || query.trim() === '') {
  throw new Error('SQL query is required');
}

// Log the start of analysis
console.log(`üîç Starting SQL injection analysis for query: ${query.substring(0, 100)}...`);

return {
  json: {
    query: query.trim(),
    max_sources: maxSources,
    include_scores: includeScores,
    context_type: contextType,
    analysis_id: `analysis_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
    timestamp: new Date().toISOString(),
    step: '1_input_validation',
    status: 'validated'
  }
};

// Process the RAG analysis response
const ragResponse = $input.all()[0].json;
const inputData = $input.all()[0].json;

// Log the RAG analysis results
console.log(`üìä RAG Analysis completed for: ${inputData.analysis_id}`);
console.log(`üéØ Answer: ${ragResponse.answer?.substring(0, 200)}...`);
console.log(`üìö Sources found: ${ragResponse.sources?.length || 0}`);

// Extract key information
const vulnerabilityDetected = ragResponse.answer?.toLowerCase().includes('injection') || 
                             ragResponse.answer?.toLowerCase().includes('vulnerable') ||
                             ragResponse.answer?.toLowerCase().includes('risk');

const riskLevel = vulnerabilityDetected ? 
  (ragResponse.answer?.toLowerCase().includes('high') ? 'HIGH' :
   ragResponse.answer?.toLowerCase().includes('medium') ? 'MEDIUM' : 'LOW') : 'NONE';

// Format sources for easier consumption
const formattedSources = ragResponse.sources?.map((source, index) => ({
  id: index + 1,
  content: source.content?.substring(0, 500) + '...',
  metadata: source.metadata,
  score: source.score,
  relevance: source.score > 0.8 ? 'HIGH' : source.score > 0.6 ? 'MEDIUM' : 'LOW'
})) || [];

return {
  json: {
    analysis_id: inputData.analysis_id,
    original_query: inputData.query,
    rag_analysis: {
      answer: ragResponse.answer,
      vulnerability_detected: vulnerabilityDetected,
      risk_level: riskLevel,
      sources_count: formattedSources.length,
      sources: formattedSources,
      timestamp: ragResponse.timestamp
    },
    step: '2_rag_analysis',
    status: 'completed'
  }
};

// Combine RAG and traditional detection results
const ragData = $input.all()[0].json.rag_analysis;
const detectionData = $input.all()[1].json;
const originalData = $input.all()[0].json;

console.log(`üîó Combining analysis results for: ${originalData.analysis_id}`);

// Compare results
const ragDetected = ragData.vulnerability_detected;
const traditionalDetected = detectionData.isVulnerable || false;
const agreement = ragDetected === traditionalDetected;

// Determine final assessment
const finalRisk = ragDetected || traditionalDetected ? 
  Math.max(
    ragData.risk_level === 'HIGH' ? 3 : ragData.risk_level === 'MEDIUM' ? 2 : ragData.risk_level === 'LOW' ? 1 : 0,
    traditionalDetected ? 2 : 0
  ) : 0;

const finalRiskLevel = finalRisk >= 3 ? 'HIGH' : finalRisk >= 2 ? 'MEDIUM' : finalRisk >= 1 ? 'LOW' : 'NONE';

// Generate recommendations
const recommendations = [];
if (ragDetected || traditionalDetected) {
  recommendations.push('üö® Potential SQL injection vulnerability detected');
  recommendations.push('üõ°Ô∏è Use parameterized queries/prepared statements');
  recommendations.push('üîç Validate and sanitize all user inputs');
  recommendations.push('üîí Apply principle of least privilege to database access');
}

if (ragData.sources_count > 0) {
  recommendations.push(`üìö Found ${ragData.sources_count} relevant security patterns for reference`);
}

return {
  json: {
    analysis_id: originalData.analysis_id,
    query: originalData.original_query,
    final_assessment: {
      vulnerability_detected: ragDetected || traditionalDetected,
      risk_level: finalRiskLevel,
      confidence: agreement ? 'HIGH' : 'MEDIUM',
      methods_agreement: agreement
    },
    rag_results: {
      detected: ragDetected,
      risk_level: ragData.risk_level,
      answer: ragData.answer,
      sources_used: ragData.sources_count,
      top_sources: ragData.sources.slice(0, 3)
    },
    traditional_detection: {
      detected: traditionalDetected,
      patterns_matched: detectionData.patterns || [],
      severity: detectionData.severity || 'UNKNOWN'
    },
    recommendations: recommendations,
    processing_time: {
      total_ms: Date.now() - new Date(originalData.timestamp).getTime(),
      completed_at: new Date().toISOString()
    },
    step: '4_final_analysis',
    status: 'completed'
  }
};

// Generate final comprehensive report
const analysisData = $input.all()[0].json;
const securityAdvice = $input.all()[1]?.json || null;

console.log(`üìã Generating final report for: ${analysisData.analysis_id}`);

// Create detailed report
const report = {
  metadata: {
    analysis_id: analysisData.analysis_id,
    timestamp: new Date().toISOString(),
    processing_time_ms: analysisData.processing_time.total_ms,
    version: '1.0.0'
  },
  query_analysis: {
    original_query: analysisData.query,
    query_length: analysisData.query.length,
    contains_keywords: {
      select: analysisData.query.toLowerCase().includes('select'),
      union: analysisData.query.toLowerCase().includes('union'),
      or_condition: analysisData.query.toLowerCase().includes("or '1'='1"),
      comment: analysisData.query.includes('--') || analysisData.query.includes('/*')
    }
  },
  vulnerability_assessment: {
    is_vulnerable: analysisData.final_assessment.vulnerability_detected,
    risk_level: analysisData.final_assessment.risk_level,
    confidence: analysisData.final_assessment.confidence,
    detection_methods: {
      rag_detection: analysisData.rag_results.detected,
      traditional_detection: analysisData.traditional_detection.detected,
      methods_agree: analysisData.final_assessment.methods_agreement
    }
  },
  rag_insights: {
    analysis_summary: analysisData.rag_results.answer?.substring(0, 500) + '...',
    sources_consulted: analysisData.rag_results.sources_used,
    relevant_patterns: analysisData.rag_results.top_sources.map(source => ({
      content_preview: source.content?.substring(0, 100) + '...',
      relevance: source.relevance,
      score: source.score
    }))
  },
  traditional_detection: {
    patterns_matched: analysisData.traditional_detection.patterns_matched,
    severity: analysisData.traditional_detection.severity
  },
  recommendations: analysisData.recommendations,
  security_advice: securityAdvice ? {
    detailed_advice: securityAdvice.answer?.substring(0, 1000) + '...',
    preventive_measures: securityAdvice.sources?.slice(0, 3).map(s => s.content?.substring(0, 200) + '...')
  } : null,
  next_steps: [
    'üîç Review the identified vulnerabilities',
    'üõ†Ô∏è Implement recommended security measures', 
    'üß™ Test fixes with additional payloads',
    'üìä Monitor for similar patterns in production',
    'üìö Update security training based on findings'
  ]
};

// Log summary
console.log(`‚úÖ Analysis complete!`);
console.log(`üéØ Vulnerability: ${report.vulnerability_assessment.is_vulnerable ? 'DETECTED' : 'NOT DETECTED'}`);
console.log(`‚ö†Ô∏è Risk Level: ${report.vulnerability_assessment.risk_level}`);
console.log(`üîó Methods Agreement: ${report.vulnerability_assessment.detection_methods.methods_agree}`);

return { json: report };

// Log error and create error response
const error = $input.all()[0].error;
const inputData = $input.all()[0].json;

console.error(`‚ùå Error in SQL injection analysis:`, error);

return {
  json: {
    error: true,
    message: 'Analysis failed',
    details: error?.message || 'Unknown error',
    analysis_id: inputData?.analysis_id || 'unknown',
    timestamp: new Date().toISOString(),
    step: 'error_handling'
  }
};
