// Simple demo queries for testing
const demoQueries = [
  "SELECT * FROM users WHERE id = '1' OR '1'='1'",
  "SELECT name FROM products WHERE category = 'electronics'",
  "UPDATE users SET password = 'hacked' WHERE 1=1; --",
  "SELECT email FROM customers WHERE city = 'Boston'"
];

// Get input data from webhook
const webhookData = $input.all()[0].json;
console.log('ðŸ” Webhook data received:', JSON.stringify(webhookData, null, 2));

// Extract query from webhook data - check multiple possible locations
const inputQuery = (typeof webhookData.query === 'string' && webhookData.query) ||
                  (typeof webhookData.body?.query === 'string' && webhookData.body?.query) ||
                  (typeof webhookData.original_input?.body?.query === 'string' && webhookData.original_input?.body?.query);

const selectedQuery = inputQuery || demoQueries[Math.floor(Math.random() * demoQueries.length)];

console.log(`ðŸŽ¯ Demo Analysis for: ${selectedQuery}`);
console.log(`ðŸ” Input query found: ${inputQuery ? 'Yes' : 'No'}`);
console.log(`ðŸ” Query source: ${inputQuery ? (webhookData.query ? 'direct' : 'body') : 'random demo'}`);

return {
  json: {
    query: selectedQuery,
    demo_mode: true,
    timestamp: new Date().toISOString(),
    original_input: webhookData,
    debug_extraction: {
      webhook_query: webhookData.query,
      body_query: webhookData.body?.query,
      nested_body_query: webhookData.original_input?.body?.query,
      selected_query: selectedQuery
    }
  }
};

// Prepare RAG request body
const inputData = $input.all()[0].json;
console.log('ðŸ”§ Preparing RAG request for query:', inputData.query);

const ragRequest = {
  query: inputData.query,
  max_sources: 3,
  include_scores: true
};

console.log('ðŸ“¦ RAG request body:', JSON.stringify(ragRequest, null, 2));

return {
  json: ragRequest
};

// Format demo results
const ragResponse = $input.all()[0].json;
const ragResult = ragResponse.data || ragResponse; // Handle different response structures

// Get the original query from the preparation step
const preparedData = $node["Prepare Demo Query"].json;
const originalQuery = preparedData.query || 'Unknown Query';

console.log('ðŸ“Š RAG Response:', JSON.stringify(ragResult, null, 2));
console.log('ðŸ” Original Query:', originalQuery);

const isVulnerable = ragResult.answer?.toLowerCase().includes('injection') || 
                   ragResult.answer?.toLowerCase().includes('vulnerable') ||
                   ragResult.answer?.toLowerCase().includes('sql') ||
                   originalQuery?.toLowerCase().includes('or') ||
                   originalQuery?.toLowerCase().includes('union');

const demoReport = {
  demo_results: {
    query_analyzed: originalQuery,
    vulnerability_detected: isVulnerable,
    rag_answer: ragResult.answer || 'No analysis available',
    sources_found: ragResult.sources?.length || 0,
    top_sources: ragResult.sources?.slice(0, 2)?.map(s => ({
      preview: s.content?.substring(0, 150) + '...' || 'No content',
      score: s.score || 0
    })) || [],
    analysis_summary: {
      risk_assessment: isVulnerable ? 'ðŸš¨ HIGH RISK' : 'âœ… SAFE',
      recommendation: isVulnerable ? 
        'Use parameterized queries and input validation' : 
        'Query appears safe, continue monitoring'
    },
    raw_rag_response: ragResult, // Include full response for debugging
    debug_info: {
      prepared_data: preparedData,
      rag_status: ragResponse.status || 'unknown'
    }
  },
  demo_info: {
    timestamp: new Date().toISOString(),
    processing_time: '< 1 second',
    workflow_version: 'Demo v1.4'
  }
};

console.log(`ðŸ“Š Demo completed: ${isVulnerable ? 'VULNERABLE' : 'SAFE'}`);

return { json: demoReport };
